.milestones {
  list-style-type: none;
  padding-left: 14.5px;
}
.milestones.milestones-bordered {
  border-bottom: 2px solid lightgrey;
  border-top: 2px solid lightgrey;
}
.milestones.milestones-bordered-top {
  border-top: 2px solid lightgrey;
}
.milestones.milestones-bordered-bottom {
  border-bottom: 2px solid lightgrey;
}
.milestones > li {
  border-left: 1px dotted lightgrey;
  border-bottom: 1px dotted lightgrey;
  padding-top: 15px;
  padding-bottom: 15px;
  padding-left: 25px;
  min-height: 50px;
}
.milestones > li h1,
.milestones > li h2,
.milestones > li h3,
.milestones > li h4,
.milestones > li h5,
.milestones > li h6 {
  margin-top: 0px;
  margin-bottom: 5px;
}
.milestones > li .list-group:last-child,
.milestones > li .panel:last-child,
.milestones > li .well:last-child,
.milestones > li .table:last-child,
.milestones > li .alert:last-child,
.milestones > li .progress:last-child,
.milestones > li pre:last-child {
  margin-bottom: 5px;
}
.milestones > li:last-child {
  border-bottom: none;
}
.milestones > li > .far:first-child,
.milestones > li > .fas:first-child,
.milestones > li > .glyphicon:first-child {
  margin-left: -39.5px;
  margin-top: -5px;
  float: left;
  position: relative;
  border-radius: 14.5px;
